	CreateInstructionLabels <shortSendNoArgs>,			<NUMSHORTSENDNOARGS>
	CreateInstructionLabels <shortSendSelfNoArgs>,		<NUMSHORTSENDSELFNOARGS>
	CreateInstructionLabels <shortSendOneArg>,			<NUMSHORTSENDONEARG>	;
