BEGINBYTECODE popStoreOuterTemp
	OuterTempPreamble
	ASSUME	edx:PTR Context		; EDX points to the Context containing the temp
	ASSUME	eax:DWORD			; EAX is pointer to 
	ASSUME	ecx:NOTHING			; ECX is 0

