@@:
	CallCPPAndLoop <BYTEPOLL>
ELSE
	movzx	ecx, BYTE PTR[_IP+eax+1]
	test	edx, edx
	lea		_IP, [_IP+eax+2]						; Offset 0 is the next instruction (after single byte instruction extension)
	jnz		@F
