BEGINBYTECODE shortSpecialSendBasicClass
	mov		ecx, [_SP]								; Load receiver into ecx
	test	cl, 1
	jne		smallInteger

	ASSUME	ecx:PTR OTE
	mov		eax, [ecx].m_oteClass					; Get class Oop	from Object into eax
	MPrefetch
	mov		[_SP], eax								; Replace stack top with class Oop
