ENDBYTECODE shortSpecialSendBasicClass

BEGINBYTECODE shortSpecialSendIsNil
	mov		eax, [oteNil]
	mov		edx, [oteFalse]
	cmp		eax, [_SP]
	cmove	edx, [oteTrue]
	MPrefetch
	mov		[_SP], edx								; Overwrite stack top with false
	DispatchNext
ENDBYTECODE shortSpecialSendIsNil

BEGINBYTECODE shortSpecialSendNotNil
	mov		eax, [oteNil]
	mov		edx, [oteFalse]
	cmp		eax, [_SP]
	cmovne	edx, [oteTrue]
