;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;
;
BEGINBYTECODE isZero
	mov		eax, [oteFalse]								; Load false (default answer)
	cmp		[_SP], SMALLINTZERO							; "receiver" is 0?
	cmove	eax, [oteTrue]
	MPrefetch
	mov		[_SP], eax									; Overwrite stack top with true/false
	DispatchNext
ENDBYTECODE isZero

;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;
;
BEGINBYTECODE shortSpecialSendNot
	mov		eax, [_SP]		
	cmp		eax, [oteFalse]
	je		isFalse

	cmp		eax, [oteTrue]
	jne		notABoolean

	; true, answer false
	add		eax, OTENTRYSIZE		; false immediately after true in OT
	MPrefetch
	mov		[_SP], eax
	DispatchNext

isFalse:
	; false, answer true
	sub		eax, OTENTRYSIZE		; true is immediately before false in OT
	MPrefetch
	mov		[_SP], eax									
	DispatchNext

notABoolean:
	SendSelectorNoArgs <Pointers.notSymbol>
ENDBYTECODE shortSpecialSendNot

;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;
;
BEGINBYTECODE shortSpecialSendNilCoalesce
	mov		eax, [_SP-OOPSIZE]
	cmp		eax, [oteNil]
	jne		@F

	; Receiver is nil, replace with argument
	mov		eax, [_SP]
	mov		[_SP-OOPSIZE], eax

@@:
	MPrefetch
	sub		_SP, OOPSIZE
	DispatchNext
ENDBYTECODE shortSpecialSendNilCoalesce

